import React, { Component } from 'react'
import { Text, View , TouchableOpacity , StyleSheet, ScrollView} from 'react-native'
import Person from './components/person'
import First from './components/first'
import Login from './components/login'
 class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      menu : ''
    }
  }

  menu1(){
    this.setState({
      menu : 'Menu 1'
    })
  }


  menu2(){
    this.setState({
      menu : 'Menu 2'
    })
  }

  menu3(){
    this.setState({
      menu : 'Menu 3'
    })
  }
  render() {
    const data = [
      {id : 1 , menu : "Menu 1"} , 
      {id : 2 , menu : "Menu 2"} , 
      {id : 3 , menu : "Menu 3"} ,
      {id : 4 , menu : "Menu 4"} , 
      {id : 5 , menu : "Menu 5"} 
  ]
    return (
      <ScrollView style={Styles.container}>
               {
                   data.map((item ) => {
                       <View key={item.id} style={Styles.item}>
                           <Text>{item.menu}</Text>
                       </View>
                   })
               }
           </ScrollView>
    )
  }
}

const Styles = StyleSheet.create({

  container : {
    padding : 10 
} ,

item : {
    padding : 10 , 
    textAlign : 'center' ,
    backgroundColor : 'green' , 
    marginTop : 10 ,
    fontWeight : 'bold' ,
    fontSize : 36
}
})

export default App
