import React , {Component} from 'react'
import { StyleSheet, Text, View , Image, ScrollView, TouchableOpacity } from 'react-native'
import HeaderInfo from "../../components/HeaderInfo"
import Layanan from '../../components/Layanan'
import Team from '../../components/Team'

  class Home extends Component{
    constructor(props) {
      super(props)
    
      this.state = {
         jenis : 'group'
      }
    }

  changeHandler(value){
    this.setState({
      jenis : value
    })
  }
    
    render(){
    return (
      <ScrollView>
        <View style={styles.container}>
          <View style={styles.wrapper}>
            <HeaderInfo />
              <View style={styles.promo}>
                <Image source={require('./Banner.png')} style={{width :380 , height : 100}} />
              </View>

            <Text style={styles.main}>Ayo <Text style={styles.maintext}>Main Futsal</Text></Text>

            <View style={styles.layanan} >
              
              <Layanan onPress={() => this.changeHandler('group')} layanan="Group" active={this.state.jenis === "group" ? true : false } />
              <Layanan onPress={() => this.changeHandler('lapangan')} layanan="lapangan" active={this.state.jenis === "lapangan" ? true : false } />
              <Layanan onPress={() => this.changeHandler('team')} layanan="team" active={this.state.jenis === "team"? true : false }  />

            </View>

            <View style={styles.sparing}>

              <View style={styles.headspring}>
                <Text>Pilih Tim <Text style={styles.sparingtext}>Sparing </Text>Kamu</Text>
              </View>

              <View style={styles.seeteam}>
                <TouchableOpacity >
                  <Text style={{color : '#ffffff'}}>Lihat Semua</Text>
                </TouchableOpacity>
              </View>

            </View>

            <ScrollView horizontal>
              <View style={styles.team}>
                <Team />
                <Team />
                <Team />
                <Team />
              </View>
            </ScrollView>
            
          </View>
        </View>
        </ScrollView>
    )
  }
}

export default Home

const styles = StyleSheet.create({
  container : {
    flex : 1 ,
    backgroundColor : '#ffffff' 
  } ,
  wrapper : {
    paddingHorizontal : 30 ,
    paddingTop : 30 
  } ,
  promo : {
    display : 'flex',
    flexDirection : 'row' ,
    marginTop : 20 ,
    marginLeft : 0 ,
    justifyContent : 'center'

  } , 
  main : {
    marginTop : 15 
  } ,

  maintext : {
    fontWeight : 'bold' ,
    fontSize : 18 ,
    
  } ,
  layanan : {
    flexDirection : 'row' ,
   justifyContent : 'space-around' ,
   marginTop : 15

  } ,
  sparing : {
    flexDirection : 'row' ,
    justifyContent : 'space-between' ,
    marginTop : 20 
  } ,
  sparingtext : {
    fontWeight : 'bold' ,
    fontSize : 18 
  } , 
  seeteam : {
    padding : 5 ,
    backgroundColor : '#797FFB' ,
    color : '#797FFB' ,
    fontSize : 15 ,
    fontWeight : 'bold' ,
    borderRadius : 5 
  } ,
  team : {
    flexDirection : 'row'
  }
})
