import React, { Component } from 'react'
import { Text, View , TouchableOpacity , StyleSheet} from 'react-native'
import First from './components/first'
 class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      menu : ''
    }
  }

  menu1(){
    this.setState({
      menu : 'Menu 1'
    })
  }


  menu2(){
    this.setState({
      menu : 'Menu 2'
    })
  }

  menu3(){
    this.setState({
      menu : 'Menu 3'
    })
  }
  render() {
    return (
      <View style={Styles.container}>

      {/* heading */}

      <Text>Belajar Membuat aplikasi sederhana menu {this.state.menu}</Text>
       <View style={Styles.underline}></View>

     {/* Bagian Content*/}

       <View style={Styles.Content}>
         <TouchableOpacity style={Styles.contentItem} onPress={() => this.menu1()}>
           <Text >Menu 1 </Text>
         </TouchableOpacity>

         <TouchableOpacity style={Styles.contentItem} onPress={() => this.menu2()}>
           <Text >Menu 2 </Text>
         </TouchableOpacity>

         <TouchableOpacity style={Styles.contentItem} onPress={() => this.menu3()}>
           <Text >Menu 3 </Text>
         </TouchableOpacity>

       </View>

       <First />

    </View>

    )
  }
}

const Styles = StyleSheet.create({

container : {
  paddingLeft : '50px' ,
  paddingRight : '50px' ,
  paddingTop : '30px' ,
  textAlign : 'center' ,
  fontSize : '20px' ,
  fontWeight : 'bold' 
} ,
underline : {
  marginTop : '30px' ,
  height : '2px' ,
  backgroundColor : 'black'
} ,
Content : {
  marginTop : '20px' ,
  display : 'flex' ,
  flexDirection : 'row' ,
  flexWrap : 'wrap' ,
  justifyContent : 'space-between' ,
  height : '80px' 
} ,

contentItem : {
  margin : '10px' ,

  textAlign : 'center' ,
  paddingTop : '15px' ,
  borderRadius : '15px' ,
  flexGrow : '1' ,
  shadowColor : '#000' ,
  shadowOffset : {
    width : 0 ,
    height : 2
  } ,
  shadowOpacity : 0.4 ,
  shadowRadius : 5,
  elevation : 8 
}
})

export default App
